operation.add:

echo '
   <script type="text/javascript"><!-- // --><![CDATA[
   window.onload = function()
   {
       var panele = document.getElementById("bbcBox_message");
       var button = document.createElement("div");
       button.setAttribute("id", "groupdocs")
       button.innerHTML = \'<img onClick="insert();" width="23" height="22" align="bottom" title="GroupDocs Viewer" alt="GroupDocs Viewer" src="/themes/default/images/bbc/viewer.gif" style="cursor: pointer; background-image: url("/themes/default/images/bbc/bbc_bg.gif");">\'
       panele.appendChild(button);
   }
   function insert() {
       var ans=prompt(\'Enter GroupDocs File ID:\',\'\');
       if(ans.length<50) { alert(\'Sorry, but this File ID is too short\'); return false; }
       if(ans.length>70) { alert(\'Sorry, but this File ID is too big\'); return false; }
       // all good continue
       var cmsName = \'SMF-Viewer\'
       var cmsVersion = \'1.0\'
       var iframe = \'[html]<iframe src="https://apps.groupdocs.com/document-viewer/embed/\'+ans+\'?&referer=\'+cmsName+\'/\'+cmsVersion+\'" frameborder="0" width="600" height="400">If you can see this text, your browser does not support iframes. Please enable iframe support in your browser or use the latest version of any popular web browser such as Mozilla Firefox or Google Chrome. For more help, please check our documentation Wiki: http://groupdocs.com/docs/display/Viewer/GroupDocs+Viewer+Integration+with+3rd+Party+Platforms</iframe>[/html]\';
       textArea = document.getElementById("message");
       textArea.value = textArea.value + iframe;
   }
   </script>';
